    static getRegion(name: string): PGRegion {
        PGRegion.initializeRegions();

        const key = name.toLowerCase();
        if (!PGRegion.regions.has(key)) {
            const regpos = PGSectors.getSectorPos(name);
            const region = new PGRegion();
            region.name = name;
            region.x0 = regpos.x * 40960;
            region.y0 = regpos.y * 40960;
            region.z0 = regpos.z * 40960;
            region.sizeX = 40960;
            region.sizeY = 40960;
            region.sizeZ = 40960;
            PGRegion.regions.set(key, region);
        }

        return PGRegion.regions.get(key)!;
    }

    static createFromData(data: IPGRegion): PGRegion {
        const region = new PGRegion();
        region.name = data.name;
        region.x0 = data.x0;
        region.y0 = data.y0;
        region.z0 = data.z0;
        region.sizeX = data.sizeX;
        region.sizeY = data.sizeY;
        region.sizeZ = data.sizeZ;
        return region;
    }
